import React, { useState, useEffect } from 'react';

const JobList = () => {
    const [jobs, setJobList] = useState([]);
    const [id, setId] = useState('');
    const [animal, setAnimal] = useState('');
    const queriedJobs =[];

    useEffect(() => {
        fetch(`/searchAll`)
        .then(response => response.json())
        .then(data => {setJobList(data);
        console.log(data);})
        .catch(error => console.error('Error fetching data:', error));
    }, [id]);


    const handleInputChangeID = (event) => {setId(event.target.value);};
    const handleInputChangeAnimal = (event) => {setAnimal(event.target.value);};

    const handleSubmit = (event) => {
        event.preventDefault();
        const updatedJobs = [];
        if (id) { findById(id, updatedJobs); }
        if (animal) { findByAnimal(animal, updatedJobs); }
        setJobList(updatedJobs);
    }
    
    const findByAnimal = (animalName, updatedJobs) => {
        fetch(`/searchByAnimal?animal=${animalName}`)
            .then(response => response.json())
            .then(data => {
                for (let i = 0; i < data.length; i++) {
                    updatedJobs.push(data[i]);
                }
                setJobList(updatedJobs); // Update jobList state here
            })
            .catch(error => console.error('Error fetching data:', error));
    };
    
    const findById = (idNumber, updatedJobs) => {
        fetch(`/searchById?id=${idNumber}`)
            .then(response => response.json())
            .then(data => {
                updatedJobs.push(data[0]);
                setJobList(updatedJobs); // Update jobList state here
            })
            .catch(error => console.error('Error fetching data:', error));
    };


    return (
        <div>
            <h1>List of Jobs</h1>
            <form onSubmit={handleSubmit}>
                <label>ID: </label>
                <input 
                    type="text" 
                    name="id" 
                    value={id} 
                    onChange={handleInputChangeID} 
                    placeholder="Enter ID"/>

                <label><br></br>Animal: </label>
                <input 
                    type="text" 
                    name="animal" 
                    value={animal} 
                    onChange={handleInputChangeAnimal} 
                    placeholder="Enter Animal Species"/>

                <button type="submit">Submit</button>
            </form>
            <table>
                <tbody>
                    {jobs.map(job => (
                        <tr key={job.id}>
                            <td>{job.id}</td>
                            <td>{job.username}</td>
                            <td>{job.animal}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default JobList;
